import React, {useState} from 'react';
import { ResponsiveNetwork } from '@nivo/network';
import data from './data.json';
import data2 from './data2.json';
import data3 from './data3.json';
import data4 from './data4.json';
import data5 from './data5.json';
import data6 from './data6.json';
const NetworkChart = () => {

  const [chartData, setData] = useState(data);
  const handleNodeClick = (node) => {
    console.log(node.id);
    const updatedData = node.id === 'Node 2' ? data2 : node.id === 'Node 3' ? data3 : node.id === 'Node 4' ? data4 : node.id === 'Node 5' ? data5 : node.id === 'Node 6' ? data6 : data;
    setData(updatedData);
  };
  return (
    <div style={{ height: '500px' }}>
      <ResponsiveNetwork
        data={chartData}
        repulsivity={30}
        iterations={10}
        centeringStrength={0.1}
        nodeBorderWidth={2}
        linkThickness={2}
        motionStiffness={160}
        motionDamping={12}
        nodeSize={20}
        activeNodeSize={30}
        inactiveNodeSize={10}
        isInteractive={true}
        onClick={(node) => handleNodeClick(node)}
        colors={'red'}
      />
    </div>
  );
};


export default NetworkChart;
