import React, {useState} from 'react';
import { ResponsiveNetwork } from '@nivo/network';
import data from './data.json';

const NetworkChart = () => {

  const [chartData, setData] = useState(data);
  const handleNodeClick = (node) => {
    console.log(node.id);
    const updatedData = {
      nodes: [
          { id: 'Node 1' },
          { id: 'Node 2' },
          { id: 'Node 3' },
          { id: 'Node 4' }
        ],
        links: [
          { source: 'Node 2', target: 'Node 3' },
          { source: 'Node 2', target: 'Node 4' }
        ]
      };
    setData(updatedData);
  };
  return (
    <div style={{ height: '500px' }}>
      <ResponsiveNetwork
        data={chartData}
        repulsivity={30}
        iterations={10}
        centeringStrength={0.1}
        nodeBorderWidth={2}
        linkThickness={2}
        motionStiffness={160}
        motionDamping={12}
        nodeSize={20}
        activeNodeSize={30}
        inactiveNodeSize={10}
        isInteractive={true}
        onClick={(node) => handleNodeClick(node)}
        colors={'red'}
      />
    </div>
  );
};


export default NetworkChart;
