import React from "react";
import { ArtistType } from "./ArtistLookup";
import "./SearchBox.css";
interface SearchItemProps {
    artist: ArtistType;
    key: number;
    onArtistChange: (artist: string) => void;
}

const SearchItem = (props: SearchItemProps) => {
    return(
        <div 
        className="search-item"
        onClick={() => 
            {
                props.onArtistChange(props.artist.id);
                document.getElementById("search-dropdown")!.style.display = "none";
                (document.getElementById("artist-input-name") as HTMLInputElement)!.value = props.artist.name + " - " + props.artist.country;
            }}
        style={
            {
                position: "relative",
                padding: "0.5vh",
                cursor: "pointer",
                backgroundColor: "white",
                zIndex: 1,
            }
        }
        onMouseEnter={(e) => {e.currentTarget.style.backgroundColor = "lightgrey";}}
        onMouseLeave={(e) => {e.currentTarget.style.backgroundColor = "white";}}    
        >
        {props.artist.name} - {props.artist.country ?? "Unknown"} 
        </div>
    );
}
export default SearchItem;
