import React, {useState} from 'react';
import { ResponsiveNetwork } from '@nivo/network';
import data from './data.json';

const NetworkChart = () => {


  const handleNodeClick = (node, event) => {
    const updatedData = { ...data };
    const clickedNode = updatedData.nodes.find((n) => n.id === node.id);
    clickedNode.color = 'red';
    setData(updatedData);
  };
  return (
    <div style={{ height: '500px' }}>
      <ResponsiveNetwork
        data={data}
        repulsivity={30}
        iterations={10}
        centeringStrength={0.1}
        nodeBorderWidth={2}
        linkThickness={2}
        motionStiffness={160}
        motionDamping={12}
        nodeSize={30}
        activeNodeSize={15}
        inactiveNodeSize={40}
        isInteractive={true}
        onClick={handleNodeClick}
      />
    </div>
  );
};


export default NetworkChart;
