import React, {useState} from 'react';
import { ResponsiveNetwork } from '@nivo/network';

const NetworkChart = (props) => {

  const chartData = props.data;
  console.log(chartData);
  const handleNodeClick = (node) => {
    console.log(node.id);
  };
  return (
    chartData ?
    <div style={{ height: '500px' }}>
      <ResponsiveNetwork
        data={chartData}
        width={1000}
        height={1000}
        repulsivity={30}
        iterations={10}
        centeringStrength={0.1}
        nodeBorderWidth={2}
        linkThickness={2}
        motionStiffness={160}
        motionDamping={12}
        nodeSize={20}
        activeNodeSize={40}
        inactiveNodeSize={10}
        isInteractive={true}
        onClick={(node) => handleNodeClick(node)}
        onMouseEnter={(node) => console.log(node)}
        colors={'red'}
      />
    </div>
    :
    <div style={{ height: '500px' }}>
      <p>
        Please wait...
      </p>
      </div>
  );
};


export default NetworkChart;
