import React, { useMemo } from "react";
import { throttle } from "lodash";
//import type { ArtistType } from "./../Data";
type ArtistType = {
    name: string;
    id: string;
    type?: string;
    country?: string;
}

type ArtistLookupResponseType = {
    created: string;
    count: number;
    offset: number;
    artists: Array<ArtistType>;
}

const ArtistLookup = async (searchQuery: string):  Promise<Array<ArtistType>>=> {
    var resultsArray: Array<ArtistType> = [];
    const UNDEFINED_PROPERTY = "Unknown";
    const LOOKUP_URL = `http://musicbrainz.org/ws/2/artist/?query=artist:${searchQuery}&fmt=json`;
    var data: ArtistLookupResponseType;
    const processData = (dataResponse: ArtistLookupResponseType): void => {   
        resultsArray = dataResponse.artists.map((artist) => {
            return {
                id: artist.id ?? UNDEFINED_PROPERTY,
                type: artist.type ?? UNDEFINED_PROPERTY,
                name: artist.name ?? UNDEFINED_PROPERTY,
                country: artist.country ?? UNDEFINED_PROPERTY,
            };
        });
    }
    // Fetches the artists from the MusicBrainz API
    try {
        const response = await fetch(LOOKUP_URL);
        data = await response.json();   
        processData(data);
    }
    catch(error){
        alert(error);
        /*data = {
