import React, {useState, useEffect} from "react";
import NetworkChart from "./NetworkChart";
import Network from "./Network";
const Data = (props) => {
    var artist_mbid = "b8a7c51f-362c-4dcb-a259-bc6e0095f0a6";
    if(props.artist !== "") {
        artist_mbid = props.artist;
    }
    const url = "https://labs.api.listenbrainz.org/similar-artists/json?algorithm=session_based_days_7500_session_300_contribution_5_threshold_10_limit_100_filter_True_skip_30&artist_mbid="+artist_mbid;
    const [similarArtists, setSimilarArtists] = useState([]);
    const [artist, setArtist] = useState("");
    const fetchData = () => {
        fetch(url)
        .then((response) => response.json())
        .then((data) => setData(data))   
    }

    const setData = (data) => {
        setArtist(data[1]);
        setSimilarArtists(data[3]);
    }

    useEffect(() => {
        fetchData();
    }, []);

    const artistList = similarArtists && similarArtists.data && (similarArtists.data.map((artist) => artist.name));
    const mainArtist = artist && artist.data && artist.data[0].name;
    artistList && artistList.push(mainArtist);

    const transformedArtists = artistList && {
        "nodes": artistList.map((artist) => {
            return{
                "id": artist,
            };
        }),
        "links": artistList.map((artist) => {
            return{
                "source": mainArtist,
                "target": artist
            };
        }),
    }


    const createGraph = () => {
        console.log(artist_mbid);
        artist_mbid = document.getElementById("artist_name").value;
        console.log(document.getElementById("artist_name").value);
    }

    return (
        <div>
            <form> 
                <label>
                    Enter artist mbid:
                </label>              
                <input type="text" id="artist_name" name="artist_name"></input>  
            </form>
            <button onClick={createGraph}>Create graph</button>
            <NetworkChart data={transformedArtists}/>
        </div>
    );
}

export default Data;
