import React, { useEffect, useMemo } from "react";
import SearchDropdown from "./SearchDropdown";
import ArtistLookup from "./ArtistLookup";
import { ArtistType } from "./ArtistLookup";
import "./SearchBox.css";
interface SearchBoxProps {
    currentsimilarArtistsLimit: number;
    onsimilarArtistsLimitChange: (similarArtistsLimit: number) => void;
    currentArtistMbid: string;
    onArtistChange: (artist_mbid: string) => void;
}

const SearchBox = (props: SearchBoxProps) => {
    // State to store the search results (list of artists)
    const [searchResults, setSearchResults] = React.useState<Array<ArtistType>>([]);
    const [searchQuery, setSearchQuery] = React.useState<string>("");
    const [similarArtistsLimit, setsimilarArtistsLimit] = React.useState<number>(props.currentsimilarArtistsLimit);
    const [artistMbid, setArtistMbid] = React.useState<string>(props.currentArtistMbid);

    // Lookup the artist based on the query
    const handleQueryChange = async (event: React.ChangeEvent<HTMLInputElement>): Promise<void> => {
        let query = event.currentTarget.value;
        if(query.trim() !== "") {
        const searchResults = await ArtistLookup(query);
        setSearchResults(searchResults ?? []);
        }
    }
    useEffect(() => {
        if(searchQuery !== "" || searchQuery.trim() !== ""){
        setSearchQuery(searchQuery);

    }
    }, [searchQuery]);
    // Set similarArtistsLimit based on user input
    const handlesimilarArtistsLimitChange = (event: React.ChangeEvent<HTMLInputElement>): void => {
        let similarArtistsLimit = event.currentTarget.valueAsNumber;
        setsimilarArtistsLimit(similarArtistsLimit);
    }
    // Set the artist and similarArtistsLimit based on user input
    useEffect(() => {
        props.onsimilarArtistsLimitChange(similarArtistsLimit);
        props.onArtistChange(artistMbid);
    }, [similarArtistsLimit, artistMbid]);

    // Hide the dropdown when the user clicks outside of it
    const toggleDropdown = () => {
        const dropdown = document.getElementById("search-dropdown");
        dropdown?.ariaExpanded === "false" ? dropdown.ariaExpanded = "true" : dropdown!.ariaExpanded = "false";
    }

    return (
        <form
        className="search-box"
        autoComplete="off"
        >
            <div
            className="artist-input"
            //onClick={(e) => {const dd = document.getElementById("search-dropdown"); dd?.style.display === "flex" ? dd.style.display = "none" : dd!.style.display = "flex";}}
            >
                <input 
                id="artist-input-name" 
                type="search" 
                aria-controls="search-dropdown"
                name="artist_mbid" 
                placeholder="Artist name" 
                onChange={(e) => {handleQueryChange(e);}}
                onFocus={toggleDropdown}
                onBlur={toggleDropdown}/>   
                <SearchDropdown searchResults={searchResults} onArtistChange={setArtistMbid}/>
            </div>
            <div
            className="graph-size-input"
            >
            <input style={{height: "auto"}}type="number" name="similarArtistsLimit" placeholder="Graph size" min="1" max="25" onChange={handlesimilarArtistsLimitChange} defaultValue={similarArtistsLimit}/>
            <span className="validity"></span>
            </div>
        </form>

    );


    /*return(
        <>
            <InputGroup>
                <Form.Control type="search" placeholder="Artist Name" onChange={handleQueryChange}/>
            </InputGroup>
            <SearchDropdown searchResults={searchResults} onArtistChange={props.onArtistChange}/>
            <InputGroup>
                <Form.Control type="number" placeholder="Graph Size" min="1" max="25" onChange={handlesimilarArtistsLimitChange} defaultValue={similarArtistsLimit}/>
            </InputGroup>
        </>
    );*/
}
export default SearchBox;
