import React, {useState} from 'react';
import { ResponsiveNetwork, NodeProps, LinkProps, NodeTooltipProps, NetworkSvgProps } from '@nivo/network';

interface GraphProps {
    data: any;
    fetchData: Function;
}

const Graph = (props: GraphProps) => {
    type Node = (typeof props.data)['nodes'][number];
    type Link = (typeof props.data)['links'][number];

    const CustomNodeComponent = ({ node }: NodeProps<Node>) => (
        <g transform={`translate(${node.x - 12},${node.y - 18})`}>
            <circle cx="12" cy="8" r="5" fill={node.color} stroke="#ffffff" />
            <path d="M3,21 h18 C 21,12 3,12 3,21" fill={node.color} stroke="#ffffff" />
        </g>
    )

    const chartProperties: NetworkSvgProps<Node, Link> = {
        data: props.data,
        height: 1000,
        width: 1000,
        repulsivity: 200,
        iterations: 120,
        centeringStrength: 0.1,
        nodeBorderWidth: 5,
        nodeColor: node => node.color,
        linkThickness: 2,
        linkColor: { from: 'source.color', modifiers: [] },
        linkDistance: node => node.distance,
        nodeSize: node => node.size,
        activeNodeSize: node => node.size * 1.5,
        inactiveNodeSize: node => node.size / 1.5,
        isInteractive: true,
        onClick: node => props.fetchData(node.data.artist_mbid)
    }

    return (
        props.data ?
        <div style={{ height: '1000px' }}>
            <ResponsiveNetwork
                {...chartProperties}
            />
        </div>
        :
        <p>
            Please wait...
        </p>
    );
}
export default Graph;
