import React, {useState, useEffect} from "react";
import NetworkChart from "./NetworkChart";

const Data = () => {
    const artist_mbid = "ef2686fe-0bdf-4e0c-b902-cc6609acc95d";
    const url = "https://labs.api.listenbrainz.org/similar-artists/json?algorithm=session_based_days_7500_session_300_contribution_5_threshold_10_limit_100_filter_True_skip_30&artist_mbid=ef2686fe-0bdf-4e0c-b902-cc6609acc95d";
    const [similarArtists, setSimilarArtists] = useState([]);
    const [artist, setArtist] = useState("");
    const fetchData = () => {
        fetch(url)
        .then((response) => response.json())
        .then((data) => setData(data))   
    }

    const setData = (data) => {
        setArtist(data[1]);
        setSimilarArtists(data[3]);
    }

    useEffect(() => {
        fetchData();
    }, []);

    const artistList = similarArtists.data && (similarArtists.data.map((artist) => artist.name));
    const mainArtist = artist.data && artist.data[0].name;
    const transformedArtists = null;
    artistList && artistList.push(mainArtist);

    const getArtists = () => {
        const transformedArtists = {
            "nodes": artistList.map((artist) => {
                return{
                    "id": artist,
                };
            }),
            "links": artistList.map((artist) => {
                return{
                    "source": mainArtist,
                    "target": artist
                };
            }),
        }
        console.log(transformedArtists);
    }

    return (
        <div>
            <button onClick={getArtists}>Get artists</button>
            <NetworkChart data={transformedArtists}/>
        </div>
    );
}

export default Data;
