import React, {useState} from 'react';
import { ResponsiveNetwork } from '@nivo/network';
import data from './data.json';

const NetworkChart = () => {

  const [chartData, setData] = useState(data);
  const handleNodeClick = () => {
    const updatedData = {
      odes: [
        { id: 'Node 1' },
        { id: 'Node 2' },
        { id: 'Node 3' },
        // ... more nodes
      ],
      links: [
        { source: 'Node 1', target: 'Node 2' },
        { source: 'Node 2', target: 'Node 3' },
        // ... more links
      ],
    };
    setData(updatedData);
  };
  return (
    <div style={{ height: '500px' }}>
      <ResponsiveNetwork
        data={chartData}
        repulsivity={30}
        iterations={10}
        centeringStrength={0.1}
        nodeBorderWidth={2}
        linkThickness={2}
        motionStiffness={160}
        motionDamping={12}
        nodeSize={30}
        activeNodeSize={15}
        inactiveNodeSize={40}
        isInteractive={true}
        onClick={handleNodeClick}
      />
    </div>
  );
};


export default NetworkChart;
