import React, { useEffect } from "react";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import SearchDropdown from "./SearchDropdown";

const SearchBx = () => {
    const [searchQuery, setSearchQuery] = React.useState<string>("");

    function handleQuery(event: React.ChangeEvent<HTMLInputElement>): void {
        let query = event.currentTarget.value;
        setSearchQuery(query);
    }

    useEffect(() => {
        if(searchQuery && searchQuery.trim() !== ""){
            let searchResults;
        }
    }, [searchQuery]);

    return(
        <>
            <InputGroup>
                <Form.Control type="search" placeholder="Artist Name" onChange={handleQuery}/>
            </InputGroup>
            <p>Search DropDown</p>
            <InputGroup>
                <Form.Control type="number" placeholder="Graph Size" />
            </InputGroup>
        </>
    );
}

export default SearchBx;
